def inputParse(input):
    map = []
    x = map
    dir = ''
    for lines in input.split('\n'):
        if lines == "":
            x = dir
            continue
        if x == map:
            x.append(list(lines))
        else:
            dir += lines
    return (map, dir)
        
def findPos(map):
    for i in range(len(map)):
        for j in range(len(map[0])):
            if map[i][j] == '@':
                return (i, j)
    return None

def printMap(map):
    for row in map:
        concat = ''.join(row)
        print(concat)

def totalMove(map, dir):
    updateMap = map
    def move(map, direction, pos, symbol):
        next = [(-1, 0), (0, 1), (1, 0), (0, -1)]
        dirIndex = None
        if direction == '^':
            nextPos  = (pos[0] + next[0][0], pos[1] + next[0][1])
            dirIndex = 0
        elif direction == '>':
            nextPos  = (pos[0] + next[1][0], pos[1] + next[1][1])
            dirIndex = 1
        elif direction == 'v':
            nextPos  = (pos[0] + next[2][0], pos[1] + next[2][1])
            dirIndex = 2
        elif direction == '<':
            nextPos  = (pos[0] + next[3][0], pos[1] + next[3][1])
            dirIndex = 3
        if map[nextPos[0]][nextPos[1]] == '#':
            return map
        elif map[nextPos[0]][nextPos[1]] == 'O':
            i = 1
            hasSpace = False
            if dirIndex == 0:
                while map[nextPos[0] - i + 1][nextPos[1]] == 'O':
                    if map[nextPos[0] - i][nextPos[1]] == '.':
                        hasSpace = True
                    i += 1
            elif dirIndex == 1:
                while map[nextPos[0]][nextPos[1] - 1 + i] == 'O':
                    if map[nextPos[0]][nextPos[1] + i] == '.':
                        hasSpace = True
                    i += 1
            elif dirIndex == 2:
                while map[nextPos[0] - 1 + i][nextPos[1]] == 'O':
                    if map[nextPos[0] + i][nextPos[1]] == '.':
                        hasSpace = True
                    i += 1
            elif dirIndex == 3:
                while map[nextPos[0]][nextPos[1] - i + 1] == 'O':
                    if  map[nextPos[0]][nextPos[1] - i] == '.':
                        hasSpace = True
                    i += 1
            if hasSpace:
                move(map, direction, nextPos, 'O')
                map[nextPos[0]][nextPos[1]] = symbol
                map[pos[0]][pos[1]] = '.'
        elif map[nextPos[0]][nextPos[1]] == '.':
            map[nextPos[0]][nextPos[1]] = symbol
            map[pos[0]][pos[1]] = '.'
        return map
    for directions in dir:
        robot = findPos(updateMap)
        updateMap = move(updateMap, directions, robot, '@')
    print(findCoordinate(updateMap))

    
    
def findCoordinate(map):
    printMap(map)
    num = 0
    coord = set()
    for i in range(len(map)):
        for j in range(len(map[0])):
            if map[i][j] == 'O':
                coord.add((i, j))
                sum = (j) + ((i) * 100)
                num += sum
    return num

        
    

